import java.util.*;
import java.io.*;
import java.math.*;


class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);

        HashMap<String,Double> students=new HashMap<>();

		int N = 10;
		for (int i = 0; i < N; i++) {
			String B = in.nextLine();
			String[] b = B.split(" ");
			String name = b[0];
			String v1 = b[1];
			String v2 = b[2];
			String v3 = b[3];

			double vote1 = Double.parseDouble(v1);
			double vote2 = Double.parseDouble(v2);
			double vote3 = Double.parseDouble(v3);
			double tot = (vote1 + vote2 + vote3) / 3;

			students.put(name,tot);
		}

		for (int i=0; i<N; i++) {
			Iterator it = students.entrySet().iterator();
			double min = 10;
			String chosenName = "";
			while (it.hasNext()) {
				Map.Entry entry = (Map.Entry) it.next();
				if ((double) (entry.getValue()) < min) {
					min = (double) (entry.getValue());
					chosenName = (String) (entry.getKey());

				}
			}
			System.out.println(chosenName);
			students.remove(chosenName);
		}


	}
}
