open_bracket = input()
close_bracket = input()
message = input()

br = {open_bracket[i]: close_bracket[i] for i in range(len(open_bracket))}
r = {o: 0 for o in open_bracket}
bracket_type_save = []


for c in message:
    if c in open_bracket:
        bracket_type_save.append(br[c])
        r[c] += 1
    elif c in close_bracket:
        if bracket_type_save.pop() != c:
            print("false")
            exit()


print("true")
print(" ".join(str(v) for v in r.values()))
