#include <bits/stdc++.h>

using namespace std;

int main(){

    int N;
    cin>>N;

    vector<string> shop;
    for(int i=0;i<N;i++){
        string item;
        cin>>item;
        shop.push_back(item);
    }
    int C;
    cin>>C;
    vector<pair<string,int>> store;
    for(int i=0;i<C;i++){
        pair<string,int> item;
        cin>>item.first>>item.second;
        store.push_back(item);
    }
    int cost=0;
    for(string s:shop){
        for(pair<string,int> i :store){
            if(i.first==s)cost+=i.second;
        }
    }
    cout<<cost<<endl;

}
