var tm = { 0:3600, 1:60, 2:1 };
var readings = [];
var str = readline;
var int = () => Number(str());

var speedLimit = int();
var cameras = Array.apply(null, new Array(int())).map(e => str());
var readingsCount = int();

for (var i = 0; i < readingsCount; i++) {

    var [plate, distance, time] = str().split(' ');
    var seconds = time.split(':').map((s, i) => tm[i] * s).reduce((a, b) => a + b);
    var last = readings.filter(r => r.plate === plate);
    distance = Number(distance);

    if (last.length > 0) {
        var ref = last[last.length - 1];
        var diff = (distance - ref.distance) / ((seconds - ref.seconds) / 3600);
        if (diff > speedLimit) {
            print(plate + ' ' + distance);
        }
    }

    readings.push({ plate: plate, distance: distance, seconds: seconds });
}

// Generator for tests
Number.prototype.toTime = function () {
    var x = (new Date(this * 1000));
    return [x.getHours(), x.getMinutes(), x.getSeconds()].map(a => a < 10 ? '0'+a : a).join(':');
}

var r = Math.random;
var f = Math.floor;
var plate = (len) => Array.apply(null, new Array(len)).map(e => r() > 0.5 ? f(r() * 10) : String.fromCharCode(97 + f(25 * r()))).join('');
var generator = (limit, cameras, vehicles, speeding) => {

    var seed = Math.floor(3600 * Math.random() * 12);
    var cars = Array.apply(null, new Array(vehicles)).map(e => plate(6).toUpperCase());
    var cams = Array.apply(null, new Array(cameras)).map((e, i) => Math.floor((i+1) * (seed / 3600 * 12)));
    var gen = [];

    cars.forEach(car => {
        var lastCam = 0;
        var lastTime = seed;
        cams.forEach((cam, i) => {
            if (i > 0) {
                var speed = limit * Math.max(0.5, Math.min(0.95, 0.5+(Math.random()*0.5)));
                if (speeding > 0) {
                    speed = limit * Math.max(1.05, Math.min(1.55, 1+Math.random()*0.5));
                    speeding--;
                }
                lastTime += speed / (cam - lastCam) * 3600;
            }
            gen.push({ car: car, cam: cam, ts: lastTime });
            lastCam = cam;
        })
    });
    var pre = [limit, cameras, cams.join('\n'), gen.length].join('\n');

    console.log(pre+'\n'+gen.sort((a, b) => a.ts > b.ts ? 1 : -1).map(a => [a.car, a.cam, Number(a.ts).toTime()].join(' ')).join('\n'));
}
