let is_prime = (n) =>{
    for(let i = 2, s = Math.sqrt(n); i <= s; i++)
        if(n % i === 0) return false; 
    return n > 1;
}
let getGoldbachSolutions = (g) => {
    let i = 2;
    let solutions = [];
    for (let j = g-i; j >= 2; j--)
    {
        if(is_prime(i) == 1 && is_prime(j) == 1)
        {
            solutions.push({a:i, b:j, d:Math.abs(j-i)});
        }
        i++;
    }
    return solutions;
}

const n = parseInt(readline());
if (n <= 2 || n % 2 != 0)
{
    console.log("OUTSIDE OF GOLDBACH");
}else{
    let solutions = getGoldbachSolutions(n).sort((a,b)=>a.d-b.d);

    let solution = solutions[0];

    console.log(Math.max(solution.a, solution.b), Math.min(solution.a, solution.b));
}
