whites = {
    'C': 0,
    'D': 2,
    'E': 4,
    'F': 5,
    'G': 7,
    'A': 9,
    'B': 11
}

accidentals = {
    'x': 2,
    '#': 1,
    '': 0,
    'b': -1,
    'bb': -2
}

for _ in range(int(input())):
    key = input()
    pitch = (whites[key[0]] + accidentals[key[1:]]) % 12
    if pitch in whites.values():
        print('white')
    else:
        print('black')
