import math

c, cb = input().split()

inputok = True
try:
    c, cb = int(c), int(cb)
    if cb >= c:
        inputok = False

except:
    inputok = False


if inputok:
    ca = c - cb
    h = math.sqrt(ca*cb)
    a = math.sqrt(h**2+ca**2)
    b = math.sqrt(h**2+cb**2)

    if str(a).find('.') > -1:
        sidea = str(a)[:str(a).find('.')+3]
    else:
        sidea = str(a) + '.00'

    if str(b).find('.') > -1:
        sideb = str(b)[:str(b).find('.')+3]
    else:
        sideb = str(b) + '.00'

    if str(h).find('.') > -1:
        height = str(h)[:str(h).find('.')+3]
    else:
        height = str(h) + '.00'

    print(sidea, sideb, height)

else:
    print('INCORRECT')
