use std::io;

macro_rules! parse_input {($x:expr, $t:ident) => ($x.trim().parse::<$t>().unwrap())}

fn main() {
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let h = parse_input!(input_line, i32);
    let mut bestHouse = 0;
    let mut besthouseValue =0;
    for i in 0..h as usize {
        let mut input_line = String::new();
        io::stdin().read_line(&mut input_line).unwrap();
        let row : Vec<i32> = input_line.trim().split_whitespace().map(|s| s.parse::<i32>().expect("err")).collect();
        let value = (row[1] - row[0]) + (row[3]-row[2]);
        if (besthouseValue < value) || bestHouse == 0{
            besthouseValue = value;
            bestHouse = i+1;
        }
    }
    println!("{}",bestHouse);
}
