int	ft_is_prime(int nb)
{
	int	i;

	i = 2;
	if (nb <= 1)
		return (0);
	else if (nb == 2)
		return (1);
	while (i <= (nb / i))
	{
		if (nb % i == 0)
		{
			return (0);
		}
		i++;
	}
	return (1);
}

int main()
{
    int num_1, num_2;
    scanf("%d", &num_1);
    scanf("%d", &num_2);
    printf("%d %d", ft_is_prime(num_1), ft_is_prime(num_2));
    return 0;
}
