using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{
	public static Dictionary <char,string> dic1 = new Dictionary<char,string>();
	public static Dictionary <char,LetterModel> dic2 = new Dictionary<char,LetterModel>();
	public static string[] Word = new String[8];
	public static void Main()
	{
		PopulateDic1();

		foreach (var kvp in dic1)
		{
			LetterModel ltr = new LetterModel(kvp.Key, kvp.Value);
			dic2.Add(kvp.Key,ltr);
		}

		string aWord = Console.ReadLine();
		foreach (char ch in aWord)
		{
			LetterModel ltr =  dic2[ch];
			Word = ltr.AddThisLetterToWord();
		}
		LetterModel.PrintWord();
	}

	public static void PopulateDic1()
	{
		dic1 = new Dictionary<char,string>();
		string alphabet = "0x1818243C42420000, A, 0x7844784444780000, B, 0x3844808044380000, C, 0x7844444444780000, D, 0x7C407840407C0000, E, 0x7C40784040400000, F, 0x3844809C44380000, G, 0x42427E4242420000, H, 0x3E080808083E0000, I, 0x1C04040444380000, J, 0x4448507048440000, K, 0x40404040407E0000, L, 0x4163554941410000, M, 0x4262524A46420000, N, 0x1C222222221C0000, O, 0x7844784040400000, P, 0x1C222222221C0200, Q, 0x7844785048440000, R, 0x1C22100C221C0000, S, 0x7F08080808080000, T, 0x42424242423C0000, U, 0x8142422424180000, V, 0x4141495563410000, W, 0x4224181824420000, X, 0x4122140808080000, Y, 0x7E040810207E0000, Z";
		var Ray = alphabet.Split(',');
		int leng = Ray.Length;

		for (int i = 0; i < leng ; i = i + 2)
		{
			string leftString = Ray[i].Trim().Substring(2); //trim it from 18 to 16
			string rightString = Ray[i+1].Trim();
			char rightChar = rightString[0];
			dic1.Add(rightChar,leftString);
		}
	}

	public class LetterModel
	{
		public char TheLetter;
		public string Str16;
		public string Str64;
		public string[] Lines = new string[8];
		public LetterModel(char ch, string str16)
		{
			TheLetter = ch;
			Str16 = str16;
			Convert16To64();
			ConvertStr64To8Lines();
		}

		public void Convert16To64()
		{
			string hexstring = Str16;
			Str64 = Convert.ToString(Convert.ToInt64(hexstring, 16), 2).PadLeft(64, '0');
		}

		public void ConvertStr64To8Lines()
		{
			for (int lin = 0 ; lin < 8 ; lin++)
			{
				Lines[lin] = Str64.Substring(lin*8,8);
			}
		}

		public string[] AddThisLetterToWord()
		{
			string[] tempRay = Word;
			for (int lin = 0 ; lin < 8 ; lin++)
			{
				tempRay[lin] = tempRay[lin] + this.Lines[lin];				
			}
			return tempRay;
		}

		public static void PrintWord()
		{
			for (int lin = 0 ; lin < 8 ; lin++)
			{
				string thisLine = Word[lin];
				thisLine = thisLine.Replace('0',' ');
				thisLine = thisLine.Replace('1','X');
                thisLine = thisLine.TrimEnd();
                if (thisLine.Length > 0) Console.WriteLine(thisLine);				
			}
		}
	}		
}
