let n = Int(readLine()!)!
let s = Int(readLine()!)!
var c = readLine()!.split(separator: " ").map { Int(String($0))! }

c += Array(repeating: 0, count: s-c.count)
var cReduced = c
var cSum = cReduced.reduce(0, +)
var avg = (cSum + n) / s
var sReduced = s
while true {
    let m = cReduced.max()!
    if  m > avg {
        cReduced.remove(at: cReduced.firstIndex(of: m)!)
        sReduced -= 1
        cSum = cReduced.reduce(0, +)
        avg = (cSum + n) / sReduced
    }
    else {
        break
    }
}
var result = [String]()
for silo in c {
    result.append(String(silo > avg ? 0  : avg - silo))
}
print(result.joined(separator: " "))

