#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main()
{
    string sNumber;
    getline(cin, sNumber);
    int nSomme = 0;
    for(int i = sNumber.size()-2 ; i>=0; --i){
        int nNombre = sNumber[i] - '0';
        int pos = sNumber.size() - i;
        if (pos % 2 == 0) nNombre *= 2;
		if (nNombre > 9) nNombre -= 9;
		nSomme += nNombre;
    }
    int res;
    (nSomme % 10 == 0)?res = 0: res = 10-(nSomme % 10);
    cout <<res << endl;
}
