int ch[50]={0};

int isPal (int nb)
{
    int c;
    int flg = 0;

    c = nb /2;
    for (int i=0;i<c;i++)
    {
        if (ch[i]==ch[nb-i]) flg=1;
    }

    return flg;
}

int base(long long N, int b)
{
    int nb=0;
    long long M;

    M=N;
    while (M!=0)
    {
        ch[nb] = M % b;
        nb++;
        M = M / b;
    }

    return nb;
}

void main()
{
    long long N;
    int b;

    scanf("%lld\n", &N);
    scanf("%d\n", &b);

    int nb;

    nb = base(N, b);
    if (nb==1)
    {
        printf("True\n");
    }
    else
    {
        if (isPal(nb) == 0)
        {
            printf("True\n");
        }
        else
        {
            printf("False\n");
        }
    }

}
