#include <bits/stdc++.h>
using namespace std;

uint16_t* lengths;
inline uint16_t computeLength(const uint32_t& n){
	if (lengths[n])return(lengths[n]);
	uint32_t res=1;
	if (n&1)
		res+=computeLength(3*n+1);
	else
		res+=computeLength(n/2);
	lengths[n]=res;
	return(res);
}

int main(void){
	lengths=(uint16_t*)malloc(1570824737*sizeof(uint16_t));
	for(uint32_t i=0;i<30;++i)lengths[1UL<<i]=i+1;
	uint32_t NbRange;
	cin >> NbRange; cin.ignore();
	for(uint32_t i=0;i<NbRange;++i){
		uint32_t a,b;
		cin >> a >> b;cin.ignore();
		uint32_t rr=0,which=0;
		for(uint32_t ii=a;ii<=b;++ii){
			uint16_t r=computeLength(ii);
			if(r>rr){
				rr=r;
				which=ii;
			}
		}
		cout << which << " " << rr << endl;
	}
	return(0);
}
