void process(vector<Areas> P){
 rect r;
 vector<vector<rect>> RR;
 int A,N = P.size(),ff=1;
 vector<int> S;
 vector<vector<int>> BB(H, vector<int> (W)),CC = BB;
 for(int i = 0; i < N; i++){BB[P[i].anchor.second][P[i].anchor.first] = 1;}
  for(int i = 0; i < N; i++){
   vector<rect> R;
   for(r.r1= 0; r.r1 < H; r.r1++){
    for(r.c1 = 0; r.c1 < W; r.c1++){
     for(r.r2 = r.r1; r.r2 < H; r.r2++){
      for(r.c2 = r.c1; r.c2 < W; r.c2++){
       if((r.r2 - r.r1 + 1)*(r.c2 - r.c1 + 1) != P[i].area){ continue; }
       int pass = 0, fail = 0;
       for(int rc = r.r1; rc <= r.r2; rc++){
		for(int cc = r.c1; cc <= r.c2; cc++){
	   if(BB[rc][cc] && rc == P[i].anchor.second && cc == P[i].anchor.first){ pass = 1; }
	   else if(BB[rc][cc]){ fail = 1; break; }}}
	if(pass == 1 && fail == 0){R.push_back(r);}}}}}
   RR.push_back(R);}
 sort(RR.begin(), RR.end(), &RRsort);
 sort(RR.begin(), RR.end(), &RR1sort);
 for(int i = 0; i < RR[0].size(); i++){
  BB = CC;
  S.clear();
  S.push_back(i);
