#include <stdio.h>

int main() {
  // variables that hold matrix values
  float a,b,c,d;
  scanf("%f %f", &a, &b);
  scanf("%f %f", &c, &d);

  // determinant of the matrix
  float det = a*d - b*c;

  // verify that the matrix is able to be inverted
  if (det == 0) {
    printf("IMPOSSIBLE\n");
    return 0;
  } else {
    // if yes, then invert matrix
    float e,f,g,h;
    e = (1/det)*d;
    f = (1/det)*-b;
    g = (1/det)*-c;
    h = (1/det)*a;
    printf("%.3f %.3f\n%.3f %.3f\n", e,f,g,h);
  }
  return 0;
}
