fermat_primes = [3,5,17,257,65537]

a, b = [int(j) for j in input().split()]

answer =sum(2**i>=a and 2**i<=b for i in range(2,31))

for i in range(1,32):
    edges = 1
    n = i
    for p in range(len(fermat_primes)):
        if n%2==1:
            edges *= fermat_primes[p]
        n //= 2

    p = 1
    while edges*p < a:
        p *= 2
    while  edges*p <= b:
        answer += 1
        p *= 2

print(answer)

