using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

public class Program
{
	public static char[,] Ray;
	public static int SIZ;

	public static void Main()
	{
		int size = Int16.Parse(Console.ReadLine());

		SIZ = size;
		Ray = new char[size,size];
		PopulateRay();
		AddBorder();
		AddX();
		AddPlus();
		PrintRay();
	}

	public static void AddBorder()
    {
		int biggestIndex = SIZ - 1;
        for (int lin = 0; lin < SIZ; lin++)
        {
            for (int pos = 0; pos < SIZ; pos++)
            {
				bool boo = (lin == 0 || lin == biggestIndex || pos == 0 || pos == biggestIndex); 
				if (boo) Ray[lin,pos] = 'X';
            }
        }
    }

	public static void AddX()
    {
		int biggestIndex = SIZ - 1;
        for (int lin = 0; lin < SIZ; lin++)
        {
            for (int pos = 0; pos < SIZ; pos++)
            {
				bool boolA = (lin == pos); 
				bool boolB = (lin == biggestIndex - pos); 
				if (boolA || boolB) Ray[lin,pos] = 'X';
            }
        }
    }

	public static void AddPlus()
    {
		int midPoint = SIZ / 2;
        for (int lin = 0; lin < SIZ; lin++)
        {
            for (int pos = 0; pos < SIZ; pos++)
            {
				bool boolA = lin == midPoint; 
				bool boolB = pos == midPoint;
				if (boolA || boolB) Ray[lin,pos] = 'X';
            }
        }
    }

	public static void PopulateRay()
    {
        for (int lin = 0; lin < SIZ; lin++)
        {
            for (int pos = 0; pos < SIZ; pos++)
            {
				Ray[lin,pos] = ' ';
            }
        }
    }

	public static void PrintRay()
    {
        for (int lin = 0; lin < SIZ; lin++)
        {
            for (int pos = 0; pos < SIZ; pos++)
            {
                Console.Write(Ray[lin,pos]);
            }
            Console.WriteLine();
        }
    }
}
