customers = int(input())

def primes(n):
    #return list of primes to n
    if n<2:
        return []
    if n==2:
        return [2]
    else:
        #calculate primes through prime factorization
        l = [2]
        for i in range(3,n+1,2):
            prime=True
            for j in l:
                sqrt = int(i**0.5)
                if sqrt < j:
                    break
                else:
                    if i%j == 0:
                        prime=False
                        break
            if prime:
                #append to use later
                l.append(i)
        return l

