using System;
class Solution
{
    static void Main(string[] args)
    {
        string input = Console.ReadLine().ToString().ToLower();
        string r = "";
        int c = 0;
        for(int i = 0; i<input.Length;i++)
        {
            if(char.IsLetter(input[i]))
                {
                    c++;
                    if(c%2==1)
                    r+= input[i].ToString().ToUpper();
                    else
                    r+= input[i].ToString();
                }
            else
                r+=input[i].ToString();
        }
        Console.WriteLine(r);
    }
}
