from itertools import product
from collections import defaultdict
import sys
import time


class KillerSudokuSolver():

    def __init__(self, grid, original_cages, cage_values):

        self.cells = dict()

        summation_groups = []
        cages = []

        # Create SudokuGroups for each row, column and box
        rows  = [SudokuGroup() for _ in range(9)]
        cols  = [SudokuGroup() for _ in range(9)]
        boxes = [SudokuGroup() for _ in range(9)]

        # Convert cells to KillerSudokuCells and add the cell to appropriate groups.
        for x in range(9):
            for y in range(9):
                cell = KillerSudokuCell(x, y, grid[y][x])
                self.cells[(x, y)] = cell

                box = (y // 3) * 3 + (x // 3)
                cell.sudoku_groups.extend([rows[y], cols[x], boxes[box]])

                rows[y].cells.append(cell)
                cols[x].cells.append(cell)
                boxes[box].cells.append(cell)

        # Convert original cages to SummationGroups with no repeats allowed.
        # All cages are included in the list of summation groups.  However, a
        # a separate list of cages is kept to be used later.
        for cage in original_cages:
            new_group = SummationGroup(cage_values[cage], repeats_allowed=False)
            for cell in original_cages[cage]:
                new_group.cells.append(self.cells[(cell)])
            summation_groups.append(new_group)
            cages.append(new_group)

        # Create summation groups by taking adjoining boxes and removing all cages
        # fully contained in those boxes.  Any remaining cells must sum to the 
        # remaining value after fully contained cages are removed.
        for height in range(1, 4):
            for width in range(1, 4):
                for y in range(4 - height):
                    for x in range(4 - width):
                        sub_group_sum = 45 * height * width
                        sub_group_cells = []
                        for h in range(height):
                            for w in range(width):
                                sub_group_cells.extend(boxes[(y+h)*3+x+w].cells[:])
                        for cage in cages:
                            if all(cell in sub_group_cells for cell in cage.cells):
                                sub_group_sum -= cage.group_sum
                                for cell in cage.cells:
                                    sub_group_cells.remove(cell)

                        if sub_group_sum not in [0, 45 * height * width]:
                            if height == 1 and width == 1:
                                repeats = False
                            else:
                                repeats = True
                            new_group = SummationGroup(sub_group_sum, repeats_allowed=repeats)
                            new_group.cells = sub_group_cells
                            summation_groups.append(new_group)

        # Create summation groups by taking adjoining rows/columns and removing all
        # cages fully contained in those rows/columns.  Any remaining cells must sum 
        # to the remaining value after fully contained cages are removed.
        for num_to_group in range(1, 9):
            for idx in range(10 - num_to_group):
                sub_group_sum = 45 * num_to_group
                sub_group_cells = []
                for i in range(num_to_group):
                    sub_group_cells.extend(rows[idx + i].cells[:])
                for cage in cages:
                    if all(cell in sub_group_cells for cell in cage.cells):
                        sub_group_sum -= cage.group_sum
                        for cell in cage.cells:
                            sub_group_cells.remove(cell)

                if sub_group_sum not in [0, 45 * num_to_group]:
                    if num_to_group == 1:
                        repeats = False
                    else:
                        repeats = True
                    new_group = SummationGroup(sub_group_sum, repeats_allowed=repeats)
                    new_group.cells = sub_group_cells
                    summation_groups.append(new_group)

                sub_group_sum = 45 * num_to_group
                sub_group_cells = []
                for i in range(num_to_group):
                    sub_group_cells.extend(cols[idx + i].cells[:])

                for cage in cages:
                    if all(cell in sub_group_cells for cell in cage.cells):
                        sub_group_sum -= cage.group_sum
                        for cell in cage.cells:
                            sub_group_cells.remove(cell)

                if sub_group_sum not in [0, 45 * num_to_group]:
                    if num_to_group == 1:
                        repeats = False
                    else:
                        repeats = True
                    new_group = SummationGroup(sub_group_sum, repeats_allowed=repeats)
                    new_group.cells = sub_group_cells
                    summation_groups.append(new_group)

        # Solve the grid by iterating over cells, summation groups and Sudoku Groups
        # looking for opportunities to reduce the number of possible values for cells.
        changes_made = True
        while changes_made:
            while changes_made:
                changes_made = False
                for cell in self.cells:
                    if self.cells[cell].reduce_cell(): 
                        changes_made = True

            if not changes_made:
                for group in summation_groups:
                    if group.reduce_group(): 
                        changes_made = True

            if not changes_made:
                for group in rows + cols + boxes:
                    if group.reduce_group(): 
                        changes_made = True


class KillerSudokuCell():

    def __init__(self, x, y, num):
        self.num = num
        self.possible_nums = [set([num]), set(range(1, 10))][num == 0]
        self.sudoku_groups = []


    def remove_num(self, num):
        self.possible_nums.remove(num)
        if len(self.possible_nums) == 1:
            self.num = (list(self.possible_nums))[0]


    def reduce_cell(self):
        changes_made = False
        if not self.num:
            for group in self.sudoku_groups:
                self.possible_nums &= group.remaining_numbers()
            if len(self.possible_nums) == 1:
                self.num = (list(self.possible_nums))[0]
                changes_made = True

        return changes_made


# Any group of cells that must add up to some number.  Rows, columns
# boxes and cages are all SummationGroups that don't allow repeats.
# A SummationGroup can also be made by looking at the aggregate of
# multiple rows or columns or boxes.  When looking at a group of
# rows, columns or boxes, repeats are allowed.
class SummationGroup():

    def __init__(self, group_sum, repeats_allowed=False):
        self.group_sum = group_sum
        self.repeats_allowed = repeats_allowed
        self.cells = []


    def remaining_numbers(self):
        remaining_nums = set()
        for cell in self.cells:
            if len(cell.possible_nums) != 1:
                remaining_nums |= cell.possible_nums

        return remaining_nums


    def reduce_group(self):
        # First, make sure all the cells are fully reduced.
        cell_changes_made = True
        while cell_changes_made:
            cell_changes_made = False
            for cell in self.cells:
                if cell.reduce_cell():
                    cell_changes_made = True

        changes_made = False
        unknown_cells = [cell for cell in self.cells if cell.num == 0]
        remaining_sum = self.group_sum - sum(cell.num for cell in self.cells if cell.num != 0)

        if len(unknown_cells) == 1:
            unknown_cells[0].num = remaining_sum
            unknown_cells[0].possible_nums = set([remaining_sum])
            changes_made = True

        if len(unknown_cells) > 1:
            for cell in unknown_cells:
                min_value = max(0, remaining_sum - sum(max(cell_2.possible_nums) for cell_2 in unknown_cells if cell_2 != cell))
                for num in list(range(1, min_value)):
                    if num in cell.possible_nums:
                        cell.remove_num(num)
                        changes_made = True

                max_value = min(9, remaining_sum - sum(min(cell_2.possible_nums) for cell_2 in unknown_cells if cell_2 != cell))
                for num in list(range(max_value + 1, 10)):
                    if num in cell.possible_nums:
                        cell.remove_num(num)
                        changes_made = True

        if not self.repeats_allowed:

            if len(unknown_cells) == 2:
                if remaining_sum % 2 == 0:
                    death_number = remaining_sum // 2
                    for cell in unknown_cells:
                        if death_number in cell.possible_nums:
                            cell.remove_num(death_number)
                            changes_made = True

                for cell in unknown_cells:
                    for num in list(cell.possible_nums):
                        for cell_2 in unknown_cells:
                            if not cell is cell_2:
                                if (remaining_sum - num) not in cell_2.possible_nums:
                                    cell.remove_num(num) 
                                    changes_made = True

            if len(unknown_cells) == 3:
                cell_1, cell_2, cell_3 = unknown_cells
                for num in list(cell_1.possible_nums):
                    target_sum = remaining_sum - num
                    possibles = [p for p in product(cell_2.possible_nums, cell_3.possible_nums) if sum(p) == target_sum and p[0] != p[1] and num not in p]
                    if len(possibles) == 0:
                        cell_1.remove_num(num)
                        changes_made = True

                for num in list(cell_2.possible_nums):
                    target_sum = remaining_sum - num
                    possibles = [p for p in product(cell_1.possible_nums, cell_3.possible_nums) if sum(p) == target_sum and p[0] != p[1] and num not in p]
                    if len(possibles) == 0:
                        cell_2.remove_num(num)
                        changes_made = True

                for num in list(cell_3.possible_nums):
                    target_sum = remaining_sum - num
                    possibles = [p for p in product(cell_1.possible_nums, cell_2.possible_nums) if sum(p) == target_sum and p[0] != p[1] and num not in p]
                    if len(possibles) == 0:
                        cell_3.remove_num(num)
                        changes_made = True

            possibles = defaultdict(list)
            if not changes_made:
                unknown_cells = [cell for cell in self.cells if not cell.num]
                for cell in unknown_cells:
                    possibles[tuple(sorted(cell.possible_nums))].append(cell)

                for possible in possibles:
                    if len(possible) == len(possibles[possible]):
                        for cell in unknown_cells:
                            if cell not in possibles[possible]:
                                for num in possible:
                                    if num in cell.possible_nums:
                                        cell.remove_num(num)
                                        changes_made = True

        return changes_made


# A SudokuGroup is a special kind SummationGroup.  The sum is always 45.
# Repeats are never allowed and the remaining numbers of the group can be
# determined a little easier than a normal summation group because a 
# SudokuGroup must contain all the numbers from 1 to 9.
class SudokuGroup(SummationGroup):

    def __init__(self):
        super().__init__(45)


    def remaining_numbers(self):
        return set(range(1, 10)) - set([cell.num for cell in self.cells if cell.num != 0])


    # Because a SudokuGroup must contain all numbers from 1 to 9, this one
    # additional group reduction technique may be used.
    def reduce_group(self):
        changes_made = super().reduce_group()
        if not changes_made:
            unknown_cells = [cell for cell in self.cells if not cell.num]
            possible_num_placements = defaultdict(list)
            for cell in unknown_cells:
                for num in cell.possible_nums:
                    possible_num_placements[num].append(cell)

            for num in possible_num_placements:
                if len(possible_num_placements[num]) == 1:
                    possible_num_placements[num][0].num = num
                    possible_num_placements[num][0].possible_nums = set([num])
                    changes_made = True
                    break

        return changes_made


# main program
start_time = time.time()
answer = [[0] * 9 for _ in range(9)]

num_grids = int(input())

raw_game_lines = [input() for _ in range(num_grids)]
raw_cage_sums = [input() for _ in range(num_grids)]

for idx in range(num_grids):

    puzzle_start = time.time()

    grid = [[0] * 9 for _ in range(9)]
    cage_grid = []

    for game_idx in range(9):
        cages_line = raw_game_lines[idx][game_idx*9:(game_idx+1)*9]
        cage_grid.append(cages_line)

    cage_values = {c:int(value) for c, value in [cage.split('=') for cage in raw_cage_sums[idx].split()]}

    cages = defaultdict(list)
    for x in range(9):
        for y in range(9):
            cages[cage_grid[y][x]].append((x, y))

    solver = KillerSudokuSolver(grid, cages, cage_values)

    for x, y in solver.cells:
        answer[y][x] += solver.cells[(x, y)].num

    print(f'\nKiller Sudoku Grid {idx+1} time = {int((time.time()-puzzle_start)*1000)} ms.\n', file=sys.stderr, flush=True)

print(f'Cumulative time = {int((time.time()-start_time)*1000)} ms.\n', file=sys.stderr, flush=True)

for row in answer:
    print(*row)
