using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;
//https://tools-unite.com/tools/random-baby-name

public class Program
{
	public static void Main()
    {
        string allKids = Console.ReadLine();
        int numOfTeams = int.Parse(Console.ReadLine());
        //emails.Split(',').Select(email => email.Trim()).ToArray()
        var KidList = allKids.Split(',').Select(allKids => allKids.Trim()).ToList();

	    var ArrayOfTeams = new List<String>[numOfTeams];

        for (int ti = 0 ; ti < numOfTeams ; ti++)
        {
            ArrayOfTeams[ti] = new List<String>();	
        }

        for (int ki = 0 ; ki < KidList.Count ; ki++)
        {
            int rem = ki % numOfTeams;
            ArrayOfTeams[rem].Add(KidList[ki]);
        }

        for (int ti = 0 ; ti < numOfTeams ; ti++)
        {
            int temp = ti + 1;
            string str = "Team " + temp + ": ";
            foreach (string name in ArrayOfTeams[ti])
            {
                str += name + ", ";	
            }
            str = str.Substring(0,str.Length - 2);
            Console.WriteLine(str);
        }
    }
}
