#include <iostream>
using namespace std;

bool shoot[99];

int main()
{
    // Read inputs
    int H; cin >> H;
    string grid[H]; for (int i=0; i<H; ++i) cin >> grid[i];

    // Compute the turns when to shoot
    int turn, maxTurns=0, X=0, W=grid[0].length();
    while (grid[H-1][X] != '^') ++X;
    for (int y=0; y<H-1; ++y)
        for (int x=0; x<W; ++x) {
            char c = grid[y][x];
            if (c != '.') {
                turn = c=='>' ? X-x-(H-y) : x-X-(H-y);
                shoot[turn] = true;
                if (turn > maxTurns) maxTurns = turn;
            }
        }

    // Print output
    for (int i=0; i<=maxTurns; ++i)
        cout << (shoot[i] ? "SHOOT" : "WAIT") << endl;
}
