#include <iostream>  

using namespace std;  

int main()  

{  

  int a=0,b=1,i,c,n,j;    



    cin>>n;    

    for(i=1; i<=n; i++)    

    {    

        a=0;    

        b=1;    

        cout<<b<<" ";  

        for(j=1; j<i; j++)    

        {    

            c=a+b;    

          cout<<c<<" ";    

            a=b;    

            b=c;  

        }    

        cout<<"\n";    

    }  

return 0;

}
