using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ClashOfCodeDominoWay
{
    public class Domino
    {
        public int Value1;
        public int Value2;

        public Domino(int value1, int value2)
        {
            this.Value1 = value1;
            this.Value2 = value2;
        }
    }

    public class Clash
    {
        public static void Main(string[] args)
        {
            DoWork(new CustomConsole());
        }

        public static void DoWork(ICustomConsole customConsole)
        {
            var n = int.Parse(customConsole.ReadLine());

            var dominos = new List<Domino>();
            for (var i = 0; i < n; i++)
            {
                var values = Array.ConvertAll(customConsole.ReadLine().Split(' '), int.Parse);
                dominos.Add(new Domino(values[0], values[1]));
            }

            var currentDomino = dominos.First();
            dominos.Remove(currentDomino);
            while(dominos.Count > 0)
            {
                Domino fusion = null;
                for (var i = 0; i < dominos.Count; i++)
                {
                    var domino = dominos[i];
                    fusion = Fusion(currentDomino, domino);
                    if (fusion != null)
                    {
                        dominos.Remove(currentDomino);
                        dominos.Remove(domino);

                        currentDomino = fusion;
                        break;
                    }
                }

                if (fusion == null)
                {
                    customConsole.WriteLine("false");
                    return;
                }
            }

            customConsole.WriteLine("true");

            // Fast way
            // Count number times
            // So, you have 7 results (for each number in 0..6)
            // If you have at least 3 odd results, it will fail
            // Otherwise, it will be ok.
        }

        private static Domino Fusion(Domino first, Domino second)
        {
            if (first.Value1 == second.Value1)
                return new Domino(first.Value2, second.Value2);
            if (first.Value1 == second.Value2)
                return new Domino(first.Value2, second.Value1);
            if (first.Value2 == second.Value1)
                return new Domino(first.Value1, second.Value2);
            if (first.Value2 == second.Value2)
                return new Domino(first.Value1, second.Value1);

            return null;
        }
    }

    public interface ICustomConsole
    {
        string ReadLine();
        void WriteLine(object o);
    }

    public class CustomConsole : ICustomConsole
    {
        public string ReadLine()
        {
            return Console.ReadLine();
        }

        public void WriteLine(object o)
        {
            Console.WriteLine(o.ToString());
        }
    }
}
