import sys
import math

# Auto-generated code below aims at helping you parse
# the standard input according to the problem statement.

n = int(input())
d = input()
m = input()

for char in m:
    if char.isalpha():
        if d=='<':
            shifted = ord(char)-n
        else:
            shifted = ord(char)+n
        if char.islower():
            if shifted < 97:
                shifted+=26
            elif shifted > 122:
                shifted-=26
        elif char.isupper():
            if shifted < 65:
                shifted+=26
            elif shifted > 90:
                shifted-=26
        print(chr(shifted),end='')
    else:
        print(' ',end='')
