#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Friend
{
    int minGroupSize, joy;
    Friend(int s, int j) : minGroupSize(s), joy(j) {}
};

int main()
{
    int N, C, P;
    cin >> N >> C >> P;
    cin.ignore();

    vector<Friend> realFriends, fakeFriends;

    for(int i = 0; i < N; ++i)
    {
        int budget, joy;
        cin >> budget >> joy;
        cin.ignore();

        // check that current friend could afford the trip if enough friends come
        if(budget >= P && (budget > P || C == 0))
        {
            int budgetLeft = budget - P; // pay individual cost
            int minGroupSize = C == 0 ? 0 : (C + budgetLeft - 1) / budgetLeft; // round up C divided by budgetLeft
            (joy >= 0 ? realFriends : fakeFriends).emplace_back(minGroupSize, joy);
        }
    }

    // sort real friends by increasing min group size
    sort(realFriends.begin(), realFriends.end(), [](const Friend& f1, const Friend& f2) {
        return f1.minGroupSize < f2.minGroupSize;
    });

    // sort fake friends by decreasing joy
    sort(fakeFriends.begin(), fakeFriends.end(), [](const Friend& f1, const Friend& f2) {
        return f1.joy > f2.joy;
    });

    /* strategy: for each K such that 1 <= K <= number of real friends, try to make a group out of
     * your K richest real friends, and add some fake friends if necessary
     */
    int groupSize = 1; // you're always part of the group
    int happiness = 0, bestHappiness = 0;

    for(Friend& f : realFriends)
    {
        // update the variables to represent the group of your K richest real friends
        ++groupSize;
        happiness += f.joy;

        if(f.minGroupSize <= groupSize)
        {
            // yay, it's possible to leave with only friends that you like!
            bestHappiness = max(bestHappiness, happiness);
        }
        else
        {
            /* oh no, you need to invite some annoying people!
             * if you need K fake friends, let's choose the least annoying K that can afford the trip
             */
            int fakeFriendsNeeded = f.minGroupSize - groupSize;
            int happiness2 = happiness;
            int groupSize2 = groupSize + fakeFriendsNeeded;
            for(Friend& f2 : fakeFriends)
            {
                // check if this fake friend can afford the trip
                if(f2.minGroupSize <= groupSize2)
                {
                    happiness2 += f2.joy;
                    // stop once we have enough fake friends
                    if(--fakeFriendsNeeded == 0)
                        break;
                }
            }
            if(fakeFriendsNeeded == 0) // you managed to invite enough people
                bestHappiness = max(bestHappiness, happiness2);
        }
    }
    cout << bestHappiness << endl;
    return 0;
}
