using System;
using System.Collections.Generic;
using System.Globalization;

namespace MyPuzzles
{
    public class DateDifference
    {
        /// <summary>
        /// получить время, прошедшее с указанной даты
        /// </summary>
        /// <param name="begin"> дата </param>
        /// <param name="years"> число полных лет </param>
        /// <param name="months"> число полных месяцев </param>
        /// <param name="days"> число дней </param>
        public static void GetTimeFrom(DateTime begin, DateTime end, out int years, out int months, out int days)
        {
            TimeSpan span = end - begin;
            if (span < TimeSpan.Zero)
            {
                years = 0;
                months = 0;
                days = 0;
            }
            else
            {
                // года
                years = end.Year - begin.Year;
                if (begin.Month > end.Month)
                {
                    years--;
                }

                // месяцы
                months = end.Month - begin.Month;
                if (begin.Day > end.Day)
                {
                    months--;
                }
                if (months < 0)
                {
                    months += 12;
                }

                // дни
                days = span.Days;
            }
        }

        public static void Main()
        {
            DateTime begin = DateTime.ParseExact(Console.ReadLine(), "dd.MM.yyyy", CultureInfo.InvariantCulture);
            DateTime end = DateTime.ParseExact(Console.ReadLine(), "dd.MM.yyyy", CultureInfo.InvariantCulture);

            int y, m, d;
            GetTimeFrom(begin, end, out y, out m, out d);

            List<string> parts = new List<string>(3);
            if (y != 0) parts.Add($"{y} year{(y == 1 ? "" : "s")}");
            if (m != 0) parts.Add($"{m} month{(m == 1 ? "" : "s")}");
            parts.Add($"total {d} day{(d == 1 ? "" : "s")}");

            Console.WriteLine(string.Join(", ", parts.ToArray()));
        }
    }
}
