import java.util.*;
import java.io.*;

class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();

        int[] hours = new int[24];
        Arrays.fill(hours, 0);

        if (in.hasNextLine()) in.nextLine();
        for (int i = 0; i < N; i++) {
            String shift = in.nextLine();
            String[] s = shift.split("->");
            int start = Integer.parseInt(s[0]);
            int end = Integer.parseInt(s[1]);
            // Counting how many guards are working for each hour
            for (int g = start; g < end; g++)
                hours[g]++;
        }
        int min = Arrays.stream(hours).min().getAsInt();
        String heist = "";

        for (int i = 0, start = 0, end = 24; i < 24; i++)
            if (hours[i] == min) {
                start = i;
                while (i < 24 && hours[i] == min)
                    i++;
                end = i;
                heist += start + "->" + end + " ";
            }
        heist = heist.substring(0, heist.length()-1); //Deleting final space
        System.out.println(heist);
    }
}
