import java.util.HashMap;
import java.util.LinkedList;
import java.util.Scanner;

class Solution {
	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		int a = in.nextInt();
		int b = in.nextInt();
		int k = in.nextInt();
		int m = in.nextInt();
		LinkedList<Integer> queue = new LinkedList<>();
		HashMap<Integer, Integer> steps = new HashMap<>();
		queue.add(k);
		steps.put(k, 0);
		while (!queue.isEmpty()) {
			int next = queue.remove();
			if (next + a <= n && !steps.containsKey(next + a)) {
				queue.add(next + a);
				steps.put(next + a, steps.get(next) + 1);
			}
			if (next - b > 0 && !steps.containsKey(next - b)) {
				queue.add(next - b);
				steps.put(next - b, steps.get(next) + 1);
			}
			if (steps.containsKey(m))
				break;
		}
		System.out.println(steps.containsKey(m) ? steps.get(m) : "IMPOSSIBLE");
	}
}
