const instruction = readline();
const message = readline();


let words = message.split(" ");
let outmessage = [];

for(let i=0; i<words.length; i++){
    const word = words[i];
    const n = words[i].length;
    let out = "";
    for(let j=0; j<n; j++){
        let k = word.charCodeAt(j);
        if(instruction == "ENCODE"){
            k = k + n;
        } else {
            k = k - n;
        }
        while(k>90){
            k -= 26;
        }
        while(k<65){
            k += 26;
        }
        out += String.fromCharCode(k);
    }
    outmessage.push(out);
}

console.log(outmessage.join(' '));
