import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        List<Integer>a=new ArrayList<>();
        for (int i = 0; i < n; i++) {
            int x = in.nextInt();
            a.add(x);
            // System.err.println(x+" = "+intToRoman(x));
        }
        Collections.sort(a, (Integer p, Integer q) -> intToRoman(p).compareTo(intToRoman(q)));
        for (int i = 0; i < n; i++) {
            System.out.print((i>0 ? " " : "") + a.get(i));
        }
    }

    static String intToRoman(int x){
        String r="";
        String[] roman="M CM D CD C XC L XL X IX V IV I".split(" ");
        int[] dec={1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1};
        for (int i = 0; i < 13; i++) {
            while(x >= dec[i]) {
                r += roman[i];
                x -= dec[i];
            }
        }
        return r;
    }
}
