#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

int main()
{
    int n;
    cin >> n; cin.ignore();
    string s;
    getline(cin, s);

    /* My algo */
    while(s.size()%n) s+=32;

    for(int i(0);i<n;++i){
        for(int k(s.size()-n);k>=0;k-=n){
            cout<<s[i+k];
        }
        cout<<endl;
    }
}
