using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace CG_Highest_building
{
    class Program
    {
        static void Main(string[] args)
        {
            var input = Console.ReadLine().Split(' ');
            int W = int.Parse(input[0]), H = int.Parse(input[1]);
            bool end = false, first = true;

            var city = new char[H][];

            for (int i = 0; i < H; i++)
                city[i] = Console.ReadLine().ToCharArray();


            for (int i = 0; i < H && !end; i++)
                for (int j = 0; j < W; j++)
                    if (city[i][j] == '#')
                    {
                        if (!first)
                            Console.WriteLine();

                        end = true;
                        first = false;

                        var left = 0;
                        while (j - left - 1 >= 0 && city[H - 1][j - left - 1] == '#')
                            left++;

                        var right = 0;
                        while (j + right + 1 < W && city[H - 1][j + right + 1] == '#')
                            right++;
                         
                        var len = 1 + left + right;

                        for (int k = i; k < H; k++)
                            Console.WriteLine(new string(city[k]).Substring(j - left, len).TrimEnd());

                        while (right > 0)
                        {
                            city[i][j + right] = ' ';
                            right--;
                        }

                    }
        }
    }
}
