appetizers = []
main_courses = []
desserts = []

budget = int(input())
nb_appetizers, nb_main_courses, nb_desserts = [int(i) for i in input().split()]
for i in range(nb_appetizers):
    name, price = input().split(',')
    appetizers.append(int(price))
for i in range(nb_main_courses):
    name, price = input().split(',')
    main_courses.append(int(price))
for i in range(nb_desserts):
    name, price = input().split(',')
    desserts.append(int(price))

most_expensive_cost = 0
for i in range(len(appetizers)):
    for j in range(len(main_courses)):
        for k in range(len(desserts)):
            cost = appetizers[i] + main_courses[j] + desserts[k]
            if cost > most_expensive_cost and cost <= budget:
                most_expensive_cost = cost

if most_expensive_cost == 0:
    print("HUNGRY")
else:
    print(budget-most_expensive_cost)
