n = int(input())
for i in range(n):
    rawname=input()
    name=''
    for ch in rawname:
        if ch not in ',.':
            name+=ch
    name=name.split()

    while name[0] in ['Mr','Mrs','Dr','Ms','Sir','Lord','Lady']:
        name=name[1:]

    while name[-1] in ['BA','MA','MD','PhD','LLB','Jr','Snr']:
        name=name[:-1]

    a=''
    for i in name:
        a+=i[0]+'.'
    print(a)
