using System;
using System.Linq;

class Solution
{
    static void Main(string[] args)
    {
        int M = int.Parse(Console.ReadLine());
        
        float result = 0f;

        int[] totalLines = new int[15 * M];
            
        for (int i = 0; i < M; i++)
        {
            int[] line = GetTemperatures(Console.ReadLine());
            
            result += ComputeFirstFormula(line);

            for (int j = 0; j < 15; j++)
            {
                totalLines[i * 15 + j] = line[j];
            }
        }
        
        result /= M;
        Console.WriteLine(Math.Round(result, 1).ToString(".0"));

        if (result >= 5 && result <= 30)
        {
            Console.WriteLine(Math.Round(ComputeSecondFormula(totalLines), 1).ToString(".0"));
        }
    }
    
    static int[] GetTemperatures(string line)
    {
        int[] temperatures = new int[15];
        
        string[] tempsStr = line.Split(' ');
        
        for (int i = 0; i < tempsStr.Length; i++)
        {
            temperatures[i] = int.Parse(tempsStr[i]);
        }
        
        return temperatures;
    }
    
    static float ComputeFirstFormula(int[] line)
    {
        return 10 + (line.Sum() - 40) / 7f;
    }

    static float ComputeSecondFormula(int[] totalLines)
    {
        float result = 0;

        for (int i = 0; i < totalLines.Length - 1; i += 2)
        {
            result += totalLines[i] + totalLines[i + 1];
        }

        return 5 + result / (totalLines.Length / 2);
    }
}
