from collections import namedtuple
import sys

class ASCII_Logo:

    screen:dict
    d={0:(1,0),90:(0,1),180:(-1,0),270:(0,-1)}

    def __init__(self):
        self.t=namedtuple('turtle',['pos','a','penDown','s'])
        self.t.pos = (0, 0)
        self.t.a = 90
        self.t.penDown = True
        self.t.s = '#'
        self.clear()

    def clear(self,ch=' '):
        self.screen={}
        self.fill=ch
        c=1e38
        self.mix=c
        self.miy=c
        self.max=-c
        self.may=-c

    def draw(self):
        if self.t.penDown:
            self.screen[self.t.pos]=self.t.s
            self.mix = min(self.mix, self.t.pos[0])
            self.max = max(self.max,self.t.pos[0])
            self.miy = min(self.miy, self.t.pos[1])
            self.may = max(self.may,self.t.pos[1])

    def move(self):
        self.draw()
        self.t.a=self.t.a%360
        x,y=self.t.pos
        dx,dy=self.d[self.t.a]
        self.t.pos=(x+dx,y+dy)

    def drawscreen(self):
        lx=1+self.max-self.mix
        ly=1+self.may-self.miy
        s=[[self.fill]*lx for y in range(ly)]
        for k,v in self.screen.items():
            x,y=k
            s[self.may-y][x+self.mix]=v
        s=[''.join(x) for x in s]
        # trim all extra trailing spaces
        s=list(map(lambda x:x.rstrip(),s))
        return '\n'.join(s)

    def run(self,pgm:list):
        tmp=[]
        for i in pgm:
            tmp +=i.split(';')
        pgm=tmp
        for i in pgm:
            print(i,file=sys.stderr)
            p = i.split()
            if i.strip()=='':
                continue
            it=p[0].upper()
            print(self.t.pos,self.t.a,p,end='',file=sys.stderr)
            if it =='CS':
                self.clear(p[1][0])
            elif it=='FD':
                for j in range(int(p[1])):
                    self.move()
            elif it=='RT':
                self.t.a -= int(p[1])
            elif it=='LT':
                self.t.a += int(p[1])
            elif it=='PU':
                self.t.penDown=False
            elif it=='PD':
                self.t.penDown=True
            elif it=='SETPC':
                self.t.s=p[1][0]
            print(self.t.pos, self.t.a,file=sys.stderr)
        return self.drawscreen()

if __name__ == '__main__':
    n=int(input())
    pgm = []
    for i in range(n):
        line = input()
        pgm.append(line)
    s=ASCII_Logo()
    print(s.run(pgm))
