import java.util.*;
import java.io.*;
import java.math.*;

class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String key = in.nextLine();
        int n = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        for (int i = 0; i < n; i++) {
            String str = in.nextLine();
            int right = 0;

            for(int j = 0; j < str.length(); j++){
                if(Character.toLowerCase(key.charAt(j)) == Character.toLowerCase(str.charAt(j))){
                    right++;  
                }
            }

            boolean passes = (float)right/(float)str.length() >= 0.9f;            
            if (passes)
                System.out.println("pass");
            else
                System.out.println("fail");
        }
    }
}
