import java.util.*;
import java.io.*;
import java.math.*;

class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int V = in.nextInt();
        int Y = in.nextInt();
        int t = (int) Math.round(Math.sqrt((float)Y*2f/9.81f));
        int X = V*t;

        System.out.println(t);
        System.out.println(X);
    }
}
