import java.util.*;
import java.math.*;
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int max = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        String sList = in.nextLine();
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }

        String[] items = new String[N];
        int[] prices = new int[N];

        for (int i = 0; i < N; i++) {
            String str = in.nextLine();
            String[] tmp = str.split(",");
            items[i] = tmp[0];
            prices[i] = Integer.parseInt(tmp[1].trim());
        }

        String[] shop = sList.split(",");

        for(String s: shop){
            for(int i=0; i < N; i++){
                if (s.trim().equals(items[i])){
                    max-=prices[i];
                    break;
                }
            }
        }
        System.out.println((max>=0)+"\n"+Math.abs(max));   
    }
}
