import java.util.Scanner;

public class Solution {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int N = in.nextInt();

		for (int i = 0; i < N; i++) {
			int a = in.nextInt();
			int b = in.nextInt();

			minDistance(a, b);

		}

	}

	// range is 0..99, inclusive
	private static void minDistance(int a, int b) {
		int up = 0;
		int down = 0;

		if (b >= a) {
			up = b - a;
			down = a + 1 + (99 - b);
		} else {
			up = (99 - a) + 1 + b;
			down = a - b;
		}

		System.out.println((up > down) ? down : up);
	}

}
