import java.util.*;
class Solution {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        String[] words = new String[n];
        for (int i = 0; i < n; i++) {
            words[i] = in.next();
        }

        String[] sortedWords = new String[n];
        for (int h = n - 1; h > -1; h--) {
            int j = 0;
            int highLength = 0;
            for (int i = 0; i < n; i++) {
                if (words[i].length() > highLength) {
                    highLength = words[i].length();
                    j = i;
                }
            }
            sortedWords[h] = words[j];
            words[j] = "";
        }

        for (int i = 0; i < n; i++) {
            System.out.println(sortedWords[i]);
        }
    }
}
