import java.util.*;
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        float percentage = in.nextInt();
        float bill = 0;
        float tip = 0;
        for (int i = 0; i < N; i++) {
            bill += in.nextFloat();
            tip += in.nextFloat();
        }
        String out = tip/bill>=(percentage/100) ? "DRUNK":"THIRSTY";
        System.out.println(out);
    }
}
