#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main()
{
    int N;
    cin >> N; cin.ignore();

    vector<int> boxes(N);
    for (int i = 0; i < N; i++) {
        cin >> boxes[i]; cin.ignore();
    }

    int K;
    cin >> K; cin.ignore();
    for (int i = 0; i < K; i++) {
        int box;
        cin >> box; cin.ignore();

        if (i > 0) cout << " ";

        int open = 1;
        int start = box;
        while (start != boxes[box - 1])
        {
            open++;
            box = boxes[box - 1];
        }
        cout << open;
    }
    cout << endl;
}
