using System;
using System.Linq;
using System.Collections.Generic;

struct Card
{   
    public static readonly string cardRanks = " A23456789TJQK"; // A = 1, T = 10, J = 11, Q = 12, K = 13

    public int Rank;
    public char Suit;

    public Card(char Rank, char Suit)
    {
        this.Rank = cardRanks.IndexOf(Rank);
        this.Suit = Suit;
    }

    public int Value
    {
        get => Rank >= cardRanks.IndexOf('J') ? 10 : Rank; // counting aces as one, face cards as ten
    }

    public static Card Parse(string input) => new Card(input[0], input[1]);
}

struct Hand
{
    public Card[] Cards;

    public Hand(Card Card1, Card Card2, Card Card3, Card Card4, Card Card5)
    {
        this.Cards = new Card[5] {Card1, Card2, Card3, Card4, Card5};
    }

    public static Hand Parse(string input)
    {
        Card[] Cards = input.Split(' ').Select(c => new Card(c[0], c[1])).ToArray();

        return new Hand(Cards[0], Cards[1], Cards[2], Cards[3], Cards[4]);
    }

    public int Score(bool isCrib = false)
    {
        int totalScore = 0;
        bool hasRunOf5 = false;
        bool hasRunOf4 = false;

        if (Cards.IsNobs()) totalScore += 1; // One point for holding the jack of the same suit as the starter card.
        if (Cards.IsFifteen()) totalScore += 2; // Two points 4 cards totalling exactly fifteen.
        if (hasRunOf5 = Cards.IsRun()) totalScore += 5; // Five points for a run of five.  Don't count runs of 4 or 3.

        if (Cards.IsFlush())
            totalScore += 5; // Five points for a flush, where all four cards in the hand are of the same suit and the starter card is also of that suit
        else if (!isCrib && Cards.Take(4).IsFlush()) // Four suited cards including the starter, but missing one of the cards in the hand, does not score for flush.  A four-card flush in the crib is not scored unless it is also the same suit as the starter card (for a total of five points).
            totalScore += 4; // Four points for a flush, where all four cards in the hand are of the same suit, not counting the starter card.

        foreach (var GroupOf4 in Cards.GetCombinations(4))  // There are 3 combinations of 4 cards.
        {
            if (GroupOf4.IsFifteen()) totalScore += 2; // Two points for each distinct combination of 4 cards totalling exactly fifteen

            if (!hasRunOf5 && GroupOf4.IsRun()) // There can be up to 2 runs of 4 cards.
            {
                totalScore += 4; // Four points for a run of four if there was no run of 5.  
                hasRunOf4 = true; // Don't count runs of three.
            }
        }

        foreach (var GroupOf3 in Cards.GetCombinations(3)) // There are 10 combinations of 3 cards.
        {
            if (GroupOf3.IsFifteen()) totalScore += 2; // Two points for each distinct combination of 3 cards totalling exactly fifteen
            if (!hasRunOf5 && !hasRunOf4 && GroupOf3.IsRun()) totalScore += 3; // Three points for a run of three consecutive cards.
        }

        foreach (var Pair in Cards.GetCombinations(2)) // There are 10 pairs of cards.
        {
            if (Pair.IsFifteen()) totalScore += 2; // Two points for each distinct combination of 2 cards totalling exactly fifteen.
            if (Pair.IsPair()) totalScore += 2; // Two points for a pair of cards of the same rank, Takes into account pair royals and double pair royals.
        }

        return totalScore;
    }
}

static class LinqExtensions
{
    public static IEnumerable<IEnumerable<T>> GetCombinations<T>(this IEnumerable<T> items, int count) // https://stackoverflow.com/questions/5132758/words-combinations-without-repetition
    {
        foreach ((var item, int i) in items.Select((x, i) => (x, i)))
            if (count == 1)
                yield return new T[] { item };
            else
                foreach (var result in GetCombinations(items.Skip(i + 1), count - 1))
                    yield return new T[] { item }.Concat(result);
    }

    public static bool IsFifteen(this IEnumerable<Card> cards) => cards.Sum(c => c.Value) == 15;
    public static bool IsRun(this IEnumerable<Card> cards) => cards.Count() >= 3 && !cards.OrderBy(c => c.Rank).Select((i,j) => i.Rank-j).Distinct().Skip(1).Any(); // https://stackoverflow.com/questions/13359327/check-if-listint32-values-are-consecutive
    public static bool IsPair(this IEnumerable<Card> cards) => cards.Count() == 2 && cards.First().Rank == cards.Last().Rank;
    public static bool IsFlush(this IEnumerable<Card> cards) => cards.Count() >= 4 && !cards.DistinctBy(c => c.Suit).Skip(1).Any();
    public static bool IsNobs(this IEnumerable<Card> cards) => cards.Count() == 5 && cards.Take(4).Any(c => c.Rank == Card.cardRanks.IndexOf('J') && c.Suit == cards.Last().Suit);
}

class Solution
{
    static void Main(string[] args)
    {
        Console.WriteLine(String.Join('\n', Enumerable.Range(0, int.Parse(Console.ReadLine())).Select(_ => Hand.Parse(Console.ReadLine()).Score())));
    }
}
