import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        List<String> rps = Arrays.asList("Rock Paper Scissors".split(" "));
        if (in.hasNextLine()) {
            in.nextLine();
        }

        String[]a=new String[n];
        for (int i = 0; i < n; i++) {
            a[i] = in.nextLine();
        }

        int startMax = 0;
        int scoreMax = 0;
        int moveMax = -1;

        for (int start = 0; start < n; start++) {
            int score = 0;
            int move = (rps.indexOf(a[start])%3+1)%3;

            for(int j = 0; j < n; j++) {
                int them = rps.indexOf(a[(j+start)%n]);
                if((them+1)%3 == move) {
                    score++;
                }else if((move+1)%3 == them){
                    j=n;
                }
            }
            // System.err.println(start + ": " + rps.get(move) + " scores " + score);
            if(score>scoreMax){
                startMax=start;
                scoreMax=score;
                moveMax=move;
            }
        }
        System.out.println(rps.get(moveMax));
        System.err.println(scoreMax);
        System.out.println(startMax);
    }
}
