text = input()
filtered_text = ""

for char in text:
    if char.lower() in "abcdefghijklmnopqrstuvwxyz0123456789 ":
        filtered_text += char.lower()

if filtered_text.replace(" ", "") == filtered_text.replace(" ", "")[::-1]:
    print("complex palindrome, " + filtered_text)
else:
    print("not a complex palindrome")
