data.solution:
WORD: for (1 .. <> ) {
  chomp( my $base = <> );
  die "Uh-oh: $base" if grep ord($_) >= 128, $base =~ /./g;
  my $l = length $base;
  for my $i (0 .. $l) {
    substr((my $insert = $base), $i, 0) = substr($base, -1-$i+int((2*$i-1)/$l), 1);
    print 1 and next WORD if $insert eq reverse $insert;
    if ($i < $l) {
      substr((my $replace = $base), $i, 1) = substr($base, -1-$i, 1);
      print 1 and next WORD if $replace eq reverse $replace;
    }
  }
  print 0;
}
