import java.util.Scanner;

public class Solution {

	static int[] arr;
	static int[] target;
	static int len;
	static int dir = 1; // 0 : forward, 1 : backward
	static String[] dirs = { " forward", " backward" };
	static int count = 0;

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		String strS = in.nextLine();
		String strT = in.nextLine();
		len = strS.length();
		arr = new int[len];
		target = new int[len];

		dir = 0;
		int c1 = process(strS, strT);

		dir = 1;
		int c2 = process(strS, strT);

		System.err.println("F=" + c1 + ", B=" + c2);
		System.out.println(Math.min(c1, c2));

	}

	private static int process(String strS, String strT) {
		count = 0;
//		String dirStr = dirs[dir];
		for (int i = 0; i < len; i++) {
			arr[i] = strS.charAt(i) - '0';
			target[i] = strT.charAt(i) - '0';
		}

		for (;;) {
			int[] options = getOptions(strS);

			if (dir == 0) {
				toggle(options[1]);
			}

			else if (dir == 1) {
				toggle(options[0]);
			}

			// printArr();

			if (solved()) {
				// System.err.println(count + dirStr);
				return count;
			}

		} // for
	}

	// test arr[] equals target[]
	private static boolean solved() {
		for (int i = 0; i < len; i++) {
			if (arr[i] != target[i]) {
				return false;
			}
		}
		return true;
	}

	private static int[] getOptions(String str) {
		int ind;
		int last = arr[len - 1]; // last bit in arr

		if (last == 1) {
			ind = len - 2;
		}
		// last is 0
		else {
			// from R to L, find the first 1
			for (ind = len - 2; ind >= 0; ind--) {
				if (arr[ind] == 1) {
					break;
				}
			}

			if (ind > 0) {
				ind--;
			} else {
				ind = -1; // negative option to skip movement at this step, step not counted
			}
		}

		int[] options = new int[2];
		options[0] = ind;
		options[1] = len - 1; // the last bit, default option
		return options;
	}

// for debug
	private static void printArr() {
		StringBuilder sb = new StringBuilder();
		for (int i = 0; i < arr.length; i++) {
			sb.append(arr[i]);
		}
		System.err.println(sb.toString());
	}

	private static void toggle(int i) {
		if (i >= 0) {
			arr[i] = (arr[i] == 1) ? 0 : 1;
			count++;
		}

		dir++;
		if (dir > 1) {
			dir = 0;
		}
	}
}
